from Crypto.Util.number import getPrime, long_to_bytes, bytes_to_long
p = getPrime(1024)
q = getPrime(1024)
n = p*q
e1 = 65537
e2 = 2*e1
flag = b"SRC{************************}"
flag = bytes_to_long(flag)

print("n =", n)
print("e1 =", e1)
print("e2 =", e2)
print("c1 =", pow(flag, e1, n))
print("c2 =", pow(flag, e2, n))

